import React from 'react'
import './App.css'
import fetch from 'node-fetch'

const today = new Date()
var ye = today.getFullYear()
var mo = today.getMonth()+1
var da = today.getDate()
var day = ye + mo + da

export default class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
    }
  }

  onClick = () => {
    fetch('https://holidays-jp.github.io/api/v1/date.json')
      .then(response => {
        return response.json()
      })
      .then(json => {
        this.setState({ 
          json: json["2020-03-20"]
        })
      })
  }

  render () {
    return (
      <div className='app'>
        <button onClick={this.onClick}>今日は祝日？</button>
        <p>{"今日は " + day}</p>
         {(() => {
          if (day === this.state.json) {
            return <span>"祝日ではない"</span>
          } else {
            return <span>"祝日"</span>
          }
        })()}
      </div>
    )
  }
}
