import React from 'react'
import './App.css'
import fetch from 'node-fetch'

const today = new Date()
var ye = today.getFullYear()
var mo = ("0"+(date.getMonth() + 1)).slice(-2)
var da = ("0" + today.getDate()).slice(-2)
var day = ye +"-"+ mo + "-" + da

export default class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      json: []
    }
  }

  onClick = () => {
    fetch('https://holidays-jp.github.io/api/v1/date.json')
      .then(response => {
        return response.json()
      })
      .then(json => {
        this.setState({ 
          json: json["2020-02-24"]
        })
      })
  }

  render () {
    return (
      <div className='app'>
        <button onClick={this.onClick}>今日は祝日？</button>
        <p>{JSON.stringify(this.state.json)}</p>
        <p>{"今日は " + day }</p>
         {(() => {
          if (day === this.state) {
            return <span>"祝日"</span>
          } else {
            return <span>"祝日ではない"</span>
          }
        })()}
      </div>
    )
  }
}
