import React from 'react'
import './App.css'
import fetch from 'fetch'

class App extends React.Component() {
  constructor (props) {
    super(props)
    this.state = {
      loading: false
    }
  }

  componentDidMount () {
    return fetch('http://weather.livedoor.com/forecast/webservice/json/v1?city=400040')
      .then(response => response.json())
      .then(responseJson => {
        this.setState({
          loading: true,
          data: responseJson
        })
      })
      .catch(error => {
        console.error(error)
      })
  }

  render () {
    if (this.state.loading) {
      return (
        <div className='App-header'>
          <p>{this.state.data}</p>
        </div>
      )
    } else {
      return (
        <div className='App-header'>
          <p>Loading...</p>
        </div>
      )
    }
  }
}

export default App
