import React from 'react'
import './App.css'
import fetch from 'node-fetch'

export default class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
    }
  }

  onClick = () => {
    fetch('https://holidays-jp.github.io/api/v1/date.json')
      .then(response => {
        return new Promise ( (resolve, reject) =>{
          resolve(response)
        })
      })
      .then(json => {
        this.setState({json:json})
        })
  }

  render () {
    return (
      <div className='app'>
        <button onClick={this.onClick}>天気取得</button>
        <p>{this.state.json}</p>
        {json.map(json => <div>{json.name}</div>)}
      </div>
    )
  }
}
