import React from 'react'
import './App.css'
import axios from 'axios'
import jsonAdapter from 'axios-jsonp'

export default class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
    }
  }

  onClick = () => {
    axios({
      url:'http://weather.livedoor.com/forecast/webservice/json/v1?city=400040c',
      adapter: jsonAdapter,
      callbackParaName: 'c'
    })
      .then(response => {
        console.log(response.json())
      })
      .then(text => {
        this.setState({
          txt: 'clicked',
          fromTxt: JSON.stringify(text)
        })
      })
      .catch(
        this.setState({
          fromTxt: 'ファイルを取得できませんでした。'
        })
      )
  }

  render () {
    return (
      <div className='app'>
        <button onClick={this.onClick}>天気取得</button>
      </div>
    )
  }
}
